import { TmplAstVariable } from '@angular/compiler';
import { Component } from '@angular/core';
import { Mobile } from '../models/mobile.model';
import { LgMobile } from './helpers/lg-mobile';
import { TV } from '../models/tv.model';
import { lgTv } from './helpers/lg-tv';

@Component({
  selector: 'app-lg',
  templateUrl: './lg.component.html',
  styleUrls: ['./lg.component.scss']
})
export class LgComponent {
  selecteddMobille: Mobile | null = null;
  selctedTV: TV | null = null;

  lgMobile: LgMobile = new LgMobile();
  lgTv: lgTv = new lgTv();

  constructor() {
    this.selecteddMobille = this.lgMobile.selectMobile('large');
    if (this.selecteddMobille) {
      console.log(this.selecteddMobille);
      console.log('model:', this.selecteddMobille.model);
      console.log('price:', this.selecteddMobille.price);
      console.log('sale tolid:', this.selecteddMobille.year);
      console.log('hedphone mikhorad:', this.selecteddMobille.hedphonekhor);

      if (this.selecteddMobille.hedphonekhor) {
        console.log('in mobile hedphone mikhorad.');
      }
      else {
        console.log('in mobile jaye hedphone nadarad.');
      }
    }

    this.selctedTV = this.lgTv.chooseTv(2020);
    if (this.selctedTV) {
      console.log(this.selctedTV);
      console.log('sale tolid:', this.selctedTV.year);
      console.log('size mahsool:', this.selctedTV.size);
      console.log('gheymat maahsool:', this.selctedTV.price);
    }
  }
}
