import { Mobile } from "src/app/models/mobile.model";

const small: string = 'small';
const medium: string = 'medium';
const large: string = 'large;';

export class LgMobile {

    selectMobile(userSize: string | null): Mobile | null {

        let mobileEnteKhabasShode: Mobile | null = null;

        if (userSize) {
            if (userSize === small) { 
                mobileEnteKhabasShode = {
                    model: 'A12',
                    year: '2020',
                    price: 80000,
                    hedphonekhor: true
                }
            }
            else if (userSize === medium) {
                mobileEnteKhabasShode = {
                    model: 'S22',
                    year: '2021',
                    price: 150000,
                    hedphonekhor: false
                }
            }
            else if (userSize === large) {
                mobileEnteKhabasShode = {
                    model: 'note10',
                    year:  '2019',
                    price: 120000,
                    hedphonekhor: true
                }
            }
            else {
                console.log('in size mojood nist.');

                mobileEnteKhabasShode = null;
            }
        }
        return mobileEnteKhabasShode;
    }
}
