import { TV } from "src/app/models/tv.model";

const tv1: number = 2018;
const tv2: number = 2020;
const tv3: number = 2022;

export class lgTv {

    chooseTv(userTv: number | null): TV | null {

        let tvEntekhabShode: TV | null = null;

        if (userTv) {
            if (userTv === tv1) {
                tvEntekhabShode = {
                    year: '2018',
                    size: 40,
                    price: 12000
                }
            }
            else if (userTv === tv2) {
                tvEntekhabShode = {
                    year: '2020',
                    size: 45,
                    price: 18000
                }
            }
            else if (userTv === tv3){
                tvEntekhabShode = {
                    year: '2022',
                    size: 50,
                    price: 25000
                }
            }
            else {
                console.log('hanooz mojood nakardim');
                tvEntekhabShode = null;
            }
        }
        return tvEntekhabShode;     
    }
}
